import { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import { Container, Card } from 'react-bootstrap';

export default function UserList() {
  const [users, setUsers] = useState([]);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    getUsers();
  }, []);

  function getUsers() {
    fetch('http://localhost:8080/user')
      .then(response => response.json())
      .then(data => setUsers(data));
  }

  function handleEditClick(user) {
    console.log(user);
    setShowModal(true);
  }

  function handleCloseModal() {
    setShowModal(false);
  }

  function handleDelete(id) {
    fetch(`http://localhost:8080/user/${id}`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then(response => {
        console.log('Data deleted succesfully');
      })
      .then(data => {
        setUsers(users.filter(user => user.id !== id));
      })
      .catch(error => {
        console.error('Error deleting data:', error);
      });
  }

  return (
    <Container>
      <Card className='bg-light'>
        <Table striped bordered hover>
          <thead className='text-white bg-dark bg-gradient'>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Age</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user.id}>
                <td className='text-black'>{user.id}</td>
                <td className='text-black'>{user.name}</td>
                <td className='text-black'>{user.age}</td>
                <td className='text-black'>
                  <Button variant="light" onClick={() => handleEditClick(user)}>Edit</Button>
                  {showModal && (
                    <div className='modal'>
                      <div className='modal-content'>
                      <span className="close" onClick={handleCloseModal}>&times;</span>
                      <p>Edit modal content goes here.</p>
                      </div>
                    </div>
                  )

                  }
                  <Button variant="danger" onClick={() => handleDelete(user.id)}>Delete</Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Card>
    </Container>
  );
}
