import { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import { Container, Card, Modal, Form } from 'react-bootstrap';

export default function UserList() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    getUsers();
  }, []);

  function getUsers() {
    fetch('http://localhost:8080/user')
      .then(response => response.json())
      .then(data => setUsers(data));
  }

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  function handleShow(user){
    setNameUpdate(user.name);
    setAgeUpdate(user.age);
    setUserUpdate({nameUpdate: user.name, ageUpdate: user.age});
    setShow(true);
  } 

  const [nameUpdate, setNameUpdate] = useState('');
  const [ageUpdate, setAgeUpdate] = useState('');
  const [userUpdate, setUserUpdate] = useState({ nameUpdate: '', ageUpdate: '' });

  function handleNameChange(event) {
    setNameUpdate(event.target.value);
  }

  function handleAgeChange(event) {
    setAgeUpdate(event.target.value);
  }

  function handleSaveChanges() {
    //setUserUpdate({nameUpdate,ageUpdate})
    console.log(userUpdate);
  }


  function handleDelete(id) {
    fetch(`http://localhost:8080/user/${id}`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then(response => {
        console.log('Data deleted succesfully', response);
      })
      .then(data => {
        setUsers(users.filter(user => user.id !== id));
      })
      .catch(error => {
        console.error('Error deleting data:', error);
      });
  }

  return (
    <Container>
      <Card className='bg-light'>
        <Table striped bordered hover>
          <thead className='text-white bg-dark bg-gradient'>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Age</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user.id}>
                <td className='text-black'>{user.id}</td>
                <td className='text-black'>{user.name}</td>
                <td className='text-black'>{user.age}</td>
                <td className='text-black'>
                  <Button variant="light" onClick={handleShow(user)}>Edit</Button>
                  <Button variant="danger" onClick={() => handleDelete(user.id)}>Delete</Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Edit User</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group controlId="formName">
                <Form.Label>Name</Form.Label>
                <Form.Control
                  type='text'
                  value={nameUpdate}
                  autoFocus
                  //placeholder={user.name}
                  onChange={handleNameChange}
                />
              </Form.Group>
              <Form.Group controlId="formAge">
                <Form.Label>Age</Form.Label>
                <Form.Control
                  type='number'
                  //placeholder={user.age}
                  value={ageUpdate}
                  onChange={handleAgeChange}
                />
              </Form.Group>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleSaveChanges}>
              Save Changes
            </Button>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </Card>
    </Container>
  );
}
